mapper.select:
select c.*
from course as c
where c.teacherID = #{id}

select sc.studentID
from studentCourse as sc
where courseID = #{id}

mapper.update:
update taskStudent set
taskID = #{id}
where taskID = #{oldID}

update taskStudent set
submitDate=#{submitDate},
fileName=#{fileName}
where studentID = #{studentID}
and taskID = #{taskID}

mapper.select:
select ts.submitDate,ts.score,ts.fileName
from taskStudent as ts
where studentID = #{studentID}
and taskID = #{taskID}

select ts.studentID,ts.submitDate,ts.fileName,ts.score
from taskStudent as ts
where taskID = #{taskID}

mapper.update:
update taskStudent set
score = #{score}
where studentID = #{studentID}
and taskID = #{taskID}

mapper.select:
select * from comment
where position = #{position}
and state is null

mapper.update:
update comment set
repliesNum = repliesNum+1
where NO = #{NO}

update comment set
state = "delete"
where NO = #{NO}

update comment set
repliesNum = repliesNum-1
where NO = #{NO}
